'use strict'

/*global describe, it */

var timelabel = require('./timelabel')
var assert = require('assert')

describe('null', function () {
  it('should equal "0 ms"', function () {
    assert.equal(timelabel(), '0 ms')
  })

  it('should equal "0 ms"', function () {
    assert.equal(timelabel(0), '0 ms')
  })
})

describe('milliseconds', function () {
  it('should equal "150 ms"', function () {
    assert.equal(timelabel(150), '150 ms')
  })
})

describe('seconds', function () {
  it('should equal "2 seconds"', function () {
    assert.equal(timelabel(2400), '2 seconds')
  })
})

describe('minutes', function () {
  it('should equal "3 minutes"', function () {
    assert.equal(timelabel(200000), '3 minutes')
  })

  it('should equal "3m 20s"', function () {
    assert.equal(timelabel(200000, true), '3m 20s')
  })
})

describe('hours', function () {
  it('should equal "11 hours"', function () {
    assert.equal(timelabel(38000000), '11 hours')
  })

  it('should equal "10h 33m"', function () {
    assert.equal(timelabel(38000000, true), '10h 33m')
  })
})

describe('days', function () {
  it('should equal "44 days"', function () {
    assert.equal(timelabel(3810000000), '44 days')
  })

  it('should equal "44d 2h"', function () {
    assert.equal(timelabel(3810000000, true), '44d 2h')
  })
})
