const request = require("node-superfetch")
const { bearer } = require("./token/bearer.json")

async function users(users) {
  if(!users) throw Error("Provide your twitter name")
  let data = await request.get("https://api.twitter.com/1.1/users/show.json")
  .set({Authorization: `Bearer ${bearer}`}).query({screen_name: users})
  return data.body;
};

module.exports = { users }